mapper.update:
UPDATE directories
SET parent_directory_id = #{parentDirectoryId}
WHERE directory_id = #{directoryId}

UPDATE directories
SET size = #{size}
WHERE directory_id = #{directoryId}

mapper.select:
SELECT EXISTS (
    SELECT 1 FROM directories
    WHERE directory_id = #{directoryId}
) AS directory_exists;

SELECT NOT EXISTS(
    SELECT 1 FROM directories WHERE parent_directory_id = #{directoryId}
    UNION ALL
    SELECT 1 FROM files WHERE directory_id = #{directoryId}
)

SELECT name
FROM directories
WHERE directory_id = #{directoryId}

SELECT directory_id
FROM directories
WHERE parent_directory_id = #{parentDirectoryId}
AND name = #{name}

SELECT COALESCE(SUM(total_size), 0) AS directory_size
FROM(
    SELECT SUM(f.size) AS total_size
    FROM files f
    WHERE f.directory_id = #{directoryId}

    UNION ALL

    SELECT SUM(d.size) AS total_size
    FROM directories d
    WHERE d.parent_directory_id = #{directoryId}
) as combined;

SELECT size
FROM directories
WHERE directory_id = #{directoryId}
